INSERT INTO activity (label, description, url, lat, lng, address) VALUES ('Avenir d''Amboise Gymnastique', 'La gym santé est une activité physique adaptée aux personnes atteintes d''affection de longue durée (diabète, obésité, cancer, maladies cardio-vasculaires...) comme au public sénior. Elle propose des exercices de gymnastique douce qui permettent d''augmenter le tonus musculaire et d''améliorer les fonctions cardio-vasculaires, en travaillant, notamment, l''équilibre, la force, la tonicité, la coordination et la souplesse.
